package tutorial;

import java.io.IOException;
import java.io.PrintWriter;

import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

@WebServlet("/lamb")
public class Lamb extends HttpServlet {
	@Override
	protected void service(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
		resp.setContentType("text/html;charset=UTF-8");
		PrintWriter out = resp.getWriter();
		String query = req.getQueryString();// ?뒷부분 의 전체 문자열을 추출

		System.out.println("query:" + query); // req.getParameter();
		// 없으면 null 이 뜸.
		// 특정이름으로 넘어온 밸류를 확인하려면...? split 을 직접할 필요는 없다.
		// WAS 가 queryString 을 분석해서 Map 형태로 처리를 해두기 때문에.
		req.getParameterMap();
		String n = req.getParameter("n"); // 이름만 주고
		String k = req.getParameter("k"); // 전부 String 으로 저장을 하고 있다.

		System.out.println("lamb : " + n + "/ soda : " + k);

		resp.setContentType("text/html;charset=UTF-8");
		out.println("<!DOCTYPE html>");
		out.println("<html>");
		out.println("<head>");
		out.print("<h1><p style=\"border: 1px solid #C9C9C9; padding: 0.3em 1em;border-radius: 2px;\">양꼬치 판매</h1></p>");
		out.println("<title>양꼬치 10개이상 음료수하나 서비스~ </title>");
		out.println("</head>");
		out.println("<img src='data:image/jpeg;base64,' width=\"400\" height=\"300\"");
		out.println("<p></p>");
		out.println("<p>10개당 콜라서비스 양꼬치[n]는 개당 12000원이구 , 음료는[k] 2000원. </p>");
		if (n == null || k == null) {
			out.print("<p>얼마나 주문할건지 요청하세요.</p>");
		} else {
			int iN = Integer.parseInt(n);
			int iK = Integer.parseInt(k);

			int answer = (iN * 12000) + ((iK - (iN / 10)) * 2000);
			out.print("<p>양꼬치가 [n]" + n + "이고 콜라가[k]" + k + "개 일때" + "요금은" + answer + "원입니다.</p>");

			out.println("</body>");
			out.print("</html>");
		}
	}
}